    ----------------------------------------------------------------
    -- Giratina color mapping
    ----------------------------------------------------------------
    procedure giratina_color(
    color_idx : in  std_logic_vector(3 downto 0);
    r_out     : out std_logic_vector(3 downto 0);
    g_out     : out std_logic_vector(3 downto 0);
    b_out     : out std_logic_vector(3 downto 0)
) is
begin
    case color_idx is
        -- 0 : transparent / background (currently black)
        when "0000" =>
            r_out := "0000"; g_out := "0000"; b_out := "0000";

        -- 1 : #282828
        when "0001" =>
            r_out := "0010"; g_out := "0010"; b_out := "0010";

        -- 2 : #383848
        when "0010" =>
            r_out := "0011"; g_out := "0011"; b_out := "0100";

        -- 3 : #484858
        when "0011" =>
            r_out := "0100"; g_out := "0100"; b_out := "0101";

        -- 4 : #A02138
        when "0100" =>
            r_out := "1001"; g_out := "0010"; b_out := "0011";

        -- 5 : #F83F50
        when "0101" =>
            r_out := "1111"; g_out := "0100"; b_out := "0101";

        -- 6 : #583010
        when "0110" =>
            r_out := "0101"; g_out := "0011"; b_out := "0001";

        -- 7 : #C0C8D0
        when "0111" =>
            r_out := "1011"; g_out := "1100"; b_out := "1100";

        -- 8 : #9090A0
        when "1000" =>
            r_out := "1000"; g_out := "1000"; b_out := "1001";

        -- 9 : #F8D000
        when "1001" =>
            r_out := "1111"; g_out := "1100"; b_out := "0000";

        -- 10 : #C89801
        when "1010" =>
            r_out := "1100"; g_out := "1001"; b_out := "0000";

        -- 11 : #715000
        when "1011" =>
            r_out := "0111"; g_out := "0101"; b_out := "0000";

        -- 12 : #F9E8C0
        when "1100" =>
            r_out := "1111"; g_out := "1110"; b_out := "1011";

        -- 13 : #686878
        when "1101" =>
            r_out := "0110"; g_out := "0110"; b_out := "0111";

        -- 14, 15 unused
        when others =>
            r_out := "0000"; g_out := "0000"; b_out := "0000";
    end case;
end procedure;


