    ----------------------------------------------------------------
    -- Pikachu color mapping (moved from chooseTeam.vhd)
    ----------------------------------------------------------------
    procedure pikachu_color(
        color_idx : in  std_logic_vector(3 downto 0);
        r_out     : out std_logic_vector(3 downto 0);
        g_out     : out std_logic_vector(3 downto 0);
        b_out     : out std_logic_vector(3 downto 0)
    ) is
    begin
        case color_idx is
            when "0001" =>  -- 1: #000000 (black)
                r_out := "0000";
                g_out := "0000";
                b_out := "0000";

            when "0010" =>  -- 2: #434343 (dark gray)
                r_out := "0100";
                g_out := "0100";
                b_out := "0100";

            when "0011" =>  -- 3: #666666 (medium gray)
                r_out := "0110";
                g_out := "0110";
                b_out := "0110";

            when "0100" =>  -- 4: #EFE04F (bright yellow)
                r_out := "1110";
                g_out := "1101";
                b_out := "0101";

            when "0101" =>  -- 5: #F1B820 (gold)
                r_out := "1110";
                g_out := "1011";
                b_out := "0010";

            when "0110" =>  -- 6: #985000 (brown)
                r_out := "1001";
                g_out := "0101";
                b_out := "0000";

            when "0111" =>  -- 7: #603007 (dark brown)
                r_out := "0110";
                g_out := "0011";
                b_out := "0000";

            when "1000" =>  -- 8: #D89000 (orange/brown)
                r_out := "1101";
                g_out := "1000";
                b_out := "0000";

            when "1001" =>  -- 9: #F8F0A0 (light yellow)
                r_out := "1111";
                g_out := "1110";
                b_out := "1001";

            when "1010" =>  -- 10: #C02018 (dark red)
                r_out := "1011";
                g_out := "0010";
                b_out := "0001";

            when "1011" =>  -- 11: #E05840 (red-orange)
                r_out := "1101";
                g_out := "0101";
                b_out := "0100";

            when "1100" =>  -- 12: #FFFFFF (white)
                r_out := "1111";
                g_out := "1111";
                b_out := "1111";

            when others =>
                -- fallback: white
                r_out := "1111";
                g_out := "1111";
                b_out := "1111";
        end case;
    end procedure pikachu_color;

